import { useState } from "react"
import { Form, Button } from "react-bootstrap"



function Contactme () {

const [firstName, setFirstName] = useState("placeholder");
const [lastName, setLastName] = useState("placeholder");

const handleInputChagne = (event) => {
    const {name, value } = event.target;

    return name === 'firstName' ? setFirstName(value) : setLastName(value)
}

const handleFormSubmit = (event) => {
event.preventDefault();

alert(`Hello ${firstName} ${lastName}`);
setFirstName("");
setLastName("");
}

return (

    <Form>
        <Form.Group className="mb-3" controlId="exampleForm.ControlInput1">
    <Form.Label>name</Form.Label>
    <Form.Control type="name" placeholder="first and last name" 

    />
  </Form.Group>
  <Form.Group className="mb-3" controlId="exampleForm.ControlInput2">
    <Form.Label>Email address</Form.Label>
    <Form.Control 
    type="text" 
    value={firstName} 
    name="firstName"
    onChange={handleInputChagne}
    placeholder="name@example.com" />
  </Form.Group>
  <Form.Group className="mb-3" controlId="exampleForm.ControlTextarea1">
    <Form.Label>Message</Form.Label>
    <Form.Control as="textarea" rows={3} />
  </Form.Group>
  <Button variant="primary" type="button" onClick={handleFormSubmit}>
    Submit
  </Button>
</Form>

 )
} 

export default Contactme
